import React, { useState } from "react";

const About = () => {
    const [showDetails, setShowDetails] = useState(false);

    const toggleDetails = () => {
        setShowDetails(!showDetails);
    };

    return (
        <div className="about-grid">
            <h1 className="about-me text-3xl font-bold mb-4 text-center">About Me</h1>
            <div className="about-card p-6 bg-[#111] text-white rounded-lg border border-[#333] ml-[80px] w-[1170px]">
                <h3 className="text-xl font-semibold mb-4 ml-[350px] text-center">Frontend Developer & UI/UX Designer</h3>
                <ul className="space-y-4 ml-[20px]">
                    <li className="text-[#b3b3b3] text-lg list-disc pl-[20px] leading-6 mb-2">
                        Hello! I'm Rao Muhammad Hamza, a dedicated computer science student with a passion for technology and a keen interest in frontend web development and graphic design.
                    </li>
                    <li className="text-[#b3b3b3] text-lg list-disc pl-[20px] leading-6 mb-2">
                        Feel free to get in touch with me through email or phone. I am excited about the opportunities that lie ahead and look forward to making a meaningful impact in the field of computer science.
                    </li>
                    {/* Read More / Show Less Button */}
                    <button
                        onClick={toggleDetails}
                        className="read-more relative w-30 h-10 mt-2 mb-5 bg-black text-white text-center leading-10 border border-orange-600 overflow-hidden"
                    >
                        <span
                            className={`absolute w-full h-full top-0 left-0 transform ${
                                showDetails ? "-translate-y-full" : "translate-y-0"
                            } transition-transform duration-400 initial`}
                        >
                            Read More
                        </span>
                        <span
                            className={`absolute w-full h-full top-0 left-0 transform ${
                                showDetails ? "translate-y-0" : "translate-y-full"
                            } transition-transform duration-400 new`}
                        >
                            Raed More
                        </span>
                    </button>
                    {/* This li will be toggled based on the state */}
                    <li className={`about-details-2 text-[#b3b3b3] text-lg list-disc pl-[20px] leading-6 mt-4 ${showDetails ? "block" : "hidden"}`}>
                        Currently, I am pursuing my degree at NFC Institute of Engineering and Technology, where I am honing my skills in various programming languages, including C, Python, and Object-Oriented Programming (OOP).
                    </li>
                </ul>
            </div>
        </div>
    );
};

export default About;
