import React, { useEffect, useState } from 'react';

const SkillSection = () => {
  const skills = [
    { name: 'HTML', percentage: 70 },
    { name: 'CSS', percentage: 60 },
    { name: 'JavaScript', percentage: 25 },
    { name: 'Tailwind', percentage: 50 },
    { name: 'React', percentage: 10 }
  ];

  // State to manage the loading progress
  const [isLoaded, setIsLoaded] = useState(false);

  useEffect(() => {
    const timer = setTimeout(() => {
      setLoaded(true); // Start animation after component is mounted
    }, 100); // Add a short delay for smoother animation start

    // Simulate percentage loading
    skills.forEach((skill, index) => {
      let currentPercent = 0;
      const interval = setInterval(() => {
        if (currentPercent <= skill.percentage) {
          setAnimatedPercentages((prev) => {
            const newPercentages = [...prev];
            newPercentages[index] = currentPercent;
            return newPercentages;
          });
          currentPercent++;
        } else {
          clearInterval(interval);
        }
      }, 15);
    });

    return () => clearTimeout(timer);
  }, [skills]);
  return (
    <section className="py-10">
      <h2 className="text-3xl font-bold text-center mb-6">Skills</h2>
      <div className="flex justify-center flex-wrap gap-10">
        {skills.map((skill, index) => (
          <div key={index} className="text-center">
            {/* Progress Bar with Animated Loading */}
            <div
              className={`relative w-32 h-32 rounded-full border-4 border-gray-700 transition-all duration-1000 ${
                isLoaded ? `bg-[conic-gradient(#FF520E ${skill.percentage}%, #333 0)]` : 'bg-[conic-gradient(#FF520E 20%, #333 0)]'
              }`}
            >
              <div className="absolute inset-0 rounded-full bg-black flex items-center justify-center">
                <span className="text-lg font-bold text-white">{skill.percentage}%</span>
              </div>
            </div>
            {/* Skill Name */}
            <p className="mt-4 font-semibold text-white">{skill.name}</p>
          </div>
        ))}
      </div>
    </section>
  );
};

export default SkillSection;
