import React, { useState } from "react";

const PortfolioSection = () => {
  const [modalImage, setModalImage] = useState(null);

  const works = [
    {
      title: "Interactive Quiz Application",
      description: "This website is for tech-based quizzes fetching data from APIs.",
      img: "/images/image1.png", // Use public folder path
      codeLink: "https://github.com/Hamxa52/Multiple-choice.github.io",
    },
    {
      title: "Portfolio Website",
      description: "This website showcases my skills, education, and ongoing projects.",
      img: "/images/image2.png",
      codeLink: "https://github.com/Hamxa52/Portfolio.github.io",
    },
    {
      title: "Weather Application on React",
      description: "This weather page shows current weather status using an API.",
      img: "/images/image3.png",
      codeLink: "https://github.com/Hamxa52/weather-app.github.io",
    },
  ];

  const openModal = (img) => setModalImage(img);

  const closeModal = () => setModalImage(null);

  return (
    <section className="py-10 bg-gray-900 text-white">
      <div className="container mx-auto">
        <h2 className="text-3xl font-bold text-center mb-8">Portfolio</h2>
        <div className="flex flex-wrap justify-center gap-10">
          {works.map((work, index) => (
            <div
              key={index}
              className="relative bg-gray-800 rounded-lg shadow-lg overflow-hidden w-80 transform hover:-translate-y-2 transition-all duration-300 cursor-pointer"
            >
              <img
                src={work.img}
                alt={work.title}
                className="w-full h-48 object-cover transition-transform duration-300 hover:scale-105"
                onClick={() => openModal(work.img)}
              />
              <div className="p-4">
                <h3 className="text-xl font-bold">{work.title}</h3>
                <p className="text-sm text-gray-400 mt-2">{work.description}</p>
                <a
                  href={work.codeLink}
                  className="inline-block mt-4 px-4 py-2 border border-orange-500 text-orange-500 hover:bg-orange-500 hover:text-white rounded transition-all duration-300"
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  Download Code
                </a>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Modal */}
      {modalImage && (
        <div
          className="fixed inset-0 bg-black bg-opacity-90 z-50 flex justify-center items-center"
          onClick={closeModal}
        >
          <img
            src={modalImage}
            alt="Work Preview"
            className="max-w-3xl max-h-3xl rounded-lg"
          />
          <button
            className="absolute top-5 right-5 text-3xl text-white hover:text-orange-500 transition-all"
            onClick={closeModal}
          >
            &times;
          </button>
        </div>
      )}
    </section>
  );
};

export default PortfolioSection;
