import { loadFull } from "tsparticles";
import { useCallback } from "react";
import Particles from "react-tsparticles";

const Background = () => {
  const particlesInit = useCallback(async (engine) => {
    await loadFull(engine); // Load the full features
  }, []);

  return (
    <Particles
  id="tsparticles"
  init={particlesInit}
  options={{
    background: {
      color: {
        value: "#000", // Set background color to black or your desired color
      },
    },
    particles: {
      shape: {
        type: "polygon", // Use polygon for custom shapes
        polygon: {
          sides: 6, // Specify number of sides for hexagon
        },
      },
      color: {
        value: "#ffffff", // Particle color
      },
      number: {
        value: 50, // Adjust the number of particles
        density: {
          enable: true,
          area: 800, // Control density of particles
        },
      },
      size: {
        value: 10, // Size of hexagonal particles
        random: true,
      },
      move: {
        enable: true,
        speed: 3,
        direction: "none",
        random: false,
        outMode: "bounce", // Particles will bounce off edges
        straight: false,
      },
      opacity: {
        value: 0.5, // Opacity of particles
      },
      links: {
        enable: false, // Turn off connecting lines
      },
    },
  }}
/>

  );
};

export default Background;
