import React, { useEffect } from "react";
import { loadFull } from "tsparticles";  // Correct import for tsparticles

const Background = () => {
  useEffect(() => {
    // Load tsparticles configuration
    loadFull().then((engine) => {
      engine.load("tsparticles", {
        particles: {
          number: {
            value: 50, // Number of particles
          },
          shape: {
            type: "polygon", // Change particle shape to hexagon
            stroke: {
              width: 2,
              color: "#e8eaed", // Stroke color
            },
            polygon: {
              sides: 6, // Hexagonal shape (6 sides)
            },
          },
          size: {
            value: 30, // Size of each hexagon
          },
          move: {
            enable: true,
            speed: 3, // Speed of movement
            direction: "none", // No specific direction
            random: true,
            outModes: {
              default: "out", // Out of bounds behavior
            },
          },
        },
        interactivity: {
          events: {
            onhover: {
              enable: true,
              mode: "repulse", // Repulse particles on hover
            },
          },
        },
        retina_detect: true,
      });
    });
  }, []);

  return <div id="tsparticles" />; // Automatically fills the screen
};

export default Background;
