import React, { useEffect, useState } from 'react';

const SkillSection = () => {
  const skills = [
    { name: 'HTML', percentage: 70 },
    { name: 'CSS', percentage: 60 },
    { name: 'JavaScript', percentage: 25 },
    { name: 'Tailwind', percentage: 50 },
    { name: 'React', percentage: 10 }
  ];

  const [animatedPercentages, setAnimatedPercentages] = useState(
    skills.map(() => 0)
  );

  useEffect(() => {
    // Simulate percentage loading
    skills.forEach((skill, index) => {
      let currentPercent = 0;
      const interval = setInterval(() => {
        if (currentPercent <= skill.percentage) {
          setAnimatedPercentages((prev) => {
            const newPercentages = [...prev];
            newPercentages[index] = currentPercent;
            return newPercentages;
          });
          currentPercent++;
        } else {
          clearInterval(interval);
        }
      }, 15);
    });
  }, []);

  return (
    <section className="py-10">
      <h2 className="text-3xl font-bold text-center mb-6">Skills</h2>
      <div className="flex justify-center flex-wrap gap-10">
        {skills.map((skill, index) => (
          <div key={index} className="text-center">
            {/* Skill Progress Circle */}
            <div
              className={`relative w-32 h-32 rounded-full border-gray-700 transition-all duration-1000`}
              style={{
                background: `conic-gradient(#FF520E ${animatedPercentages[index] * 3.6}deg, #333 0deg)`
              }}
            >
              <div className="absolute inset-0 rounded-full bg-black flex items-center justify-center border-x-#FF520E">
                <span className="text-lg font-bold text-white">
                  {animatedPercentages[index]}%
                </span>
              </div>
            </div>
            {/* Skill Name */}
            <p className="mt-4 font-semibold text-white">{skill.name}</p>
          </div>
        ))}
      </div>
    </section>
  );
};

export default SkillSection;
