import React, { useState } from "react";

const About = () => {
    const [showDetails, setShowDetails] = useState(false);

    const toggleDetails = () => {
        setShowDetails(!showDetails);
    };

    return (
        <div className="about-grid">
        <h1 className="about-me">About Me</h1>
            <div className="about card fade in">
            <h3>Frontend Developer & UI/UX Design</h3>
            <ul>
                <li>Hello! I'm Rao Muhammad Hamza , a dedicated computer science student with a
                    passion for technology and a keen interest in frontend web development , and graphic
                    design.</li>
                <li>Feel free to get in touch with me through email or phone . I am excited about
                    the opportunities that lie ahead and look forward to making a meaningful impact in the field of
                    computer science.</li>
                <button class="read-more" onclick="toggleDetails(this, document.querySelector('.about-details-2'))">
                    <span class="initial">Read More</span>
                    <span class="new">Read More</span>
                </button>
                <li class="about-details-2">Currently, I am pursuing my degree at NFC Institute of Engineering and
                    Technology,
                    where I am honing my skills in various programming languages, including C,
                    Python and Object-Oriented Programming (OOP).</li>
            </ul>
            </div>
        </div>
    );
};

export default About;
