import React, { useState } from "react";

const About = () => {
    const [showDetails, setShowDetails] = useState(false);

    const toggleDetails = () => {
        setShowDetails(!showDetails);
    };

    return (
        <div id="particles-services" className="absolute w-full h-full -z-10">
            <div className="grid" id="about">
                <h1 className="text-2xl md:text-4xl text-center mt-2 mb-4">About Me</h1>
                <div className="max-w-screen-lg bg-gray-900 p-5 md:p-8 rounded-lg border border-gray-700 text-white mx-auto">
                    <h3 className="text-lg md:text-xl font-semibold mb-4">Frontend Developer & UI/UX Design</h3>
                    <ul className="space-y-4 text-gray-400 text-sm md:text-base">
                        <li>
                            Hello! I'm Rao Muhammad Hamza, a dedicated computer science
                            student with a passion for technology and a keen interest in
                            frontend web development and graphic design.
                        </li>
                        <li>
                            Feel free to get in touch with me through email or phone. I am
                            excited about the opportunities that lie ahead and look forward to
                            making a meaningful impact in the field of computer science.
                        </li>
                    </ul>
                    <button
                        className="bg-transparent border border-gray-600 text-white rounded px-4 py-2 mt-4 hover:bg-gray-700 transition"
                        onClick={toggleDetails}
                    >
                        {showDetails ? "Show Less" : "Read More"}
                    </button>
                    {showDetails && (
                        <p className="mt-4 text-gray-400">
                            Currently, I am pursuing my degree at NFC Institute of Engineering
                            and Technology, where I am honing my skills in various programming
                            languages, including C, Python, and Object-Oriented Programming
                            (OOP).
                        </p>
                    )}
                </div>
            </div>
        </div>
    );
};

export default About;
