import React, { useEffect, useState } from 'react';

const SkillSection = () => {
  const skills = [
    { name: 'HTML', percentage: 70 },
    { name: 'CSS', percentage: 60 },
    { name: 'JavaScript', percentage: 25 },
    { name: 'Tailwind', percentage: 50 },
    { name: 'React', percentage: 10 }
  ];

  // State to manage the loading progress
  const [isLoaded, setIsLoaded] = useState(false);

  useEffect(() => {
    // Trigger the loading effect after the component mounts
    setIsLoaded(true);
  }, []);

  return (
    <section className="py-10">
      <h2 className="text-3xl font-bold text-center mb-6">Skills</h2>
      <div className="flex justify-center flex-wrap gap-10">
        {skills.map((skill, index) => (
          <div key={index} className="text-center">
            {/* Progress Bar with Animated Loading */}
            <div
              className={`relative w-32 h-32 rounded-full border-4 border-gray-700 transition-all duration-1000 ${
                isLoaded
                  ? `bg-[conic-gradient(#FF520E ${skill.percentage}%, #333 0)]`
                  : 'bg-[conic-gradient(#FF520E 0%, #333 0)]' // Start from 0% until loaded
              }`}
            >
              <div className="absolute inset-0 rounded-full bg-black flex items-center justify-center">
                <span className="text-lg font-bold text-white">{isLoaded ? `${skill.percentage}%` : "0%"}</span>
              </div>
            </div>
            {/* Skill Name */}
            <p className="mt-4 font-semibold text-white">{skill.name}</p>
          </div>
        ))}
      </div>
    </section>
  );
};

export default SkillSection;
